insert into BOOK_TB(created_date, modified_date, publisher_id, title, author, price, introduction, small_category_id, author_info, epub_id, cover_id, status)
values
('2023-02-01 00:01', '2021-01-01 00:01', 1, '성공의 법칙', '제임스 스미스', 15000, '성공의 원칙과 철학을 전달하는 자기계발서.', 1, '제임스 스미스는 성공한 사업가로서 인생의 원칙을 전달하는 저자이다.', 1, 2, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 2, '인간관계의 기술', '토마스 존슨', 18000, '인간관계의 기초를 알려주는 심리학적 접근의 자기계발서.', 2, '토마스 존슨은 인간관계 전문가로 다양한 강연을 진행하고 있다.', 3, 4, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 3, '마법의 말하기', '리사 제이', 17000, '마법처럼 사람들을 사로잡는 화술과 협상의 기술을 소개하는 책.', 3, '리사 제이는 전 세계적으로 유명한 화술 전문가이다.', 5, 6, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 4, '시간관리의 비결', '마이클 스톤', 20000, '시간관리의 비결을 알려주는 실용적인 자기계발서.', 4, '마이클 스톤은 시간관리 전문가로 베스트셀러 저자이다.', 7, 8, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 5, '진정한 리더의 조건', '칼 레이너', 21000, '진정한 리더의 조건과 리더십의 발전을 위한 조언을 담은 책.', 5, '칼 레이너는 리더십에 대한 연구를 통해 다양한 인사이트를 제공한다.', 9, 10, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 6, '세계의 아름다운 여행지', '마크 타워', 19000, '세계의 아름다운 여행지를 소개하는 여행 에세이.', 6, '마크 타워는 여행 전문가로 세계의 아름다운 명소를 소개한다.', 11, 12, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 1, '예술의 순간들', '에밀리 브룩스', 16000, '예술과 창작의 과정에서 얻는 영감과 통찰을 담은 에세이.', 7, '에밀리 브룩스는 예술 전문 에세이스트로, 작가와 화가 등 다양한 예술가들과의 인터뷰를 진행한다.', 13, 14, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 2, '책읽기의 즐거움', '다니엘 파커', 16000, '독서의 즐거움과 그 속에서 얻을 수 있는 인생의 교훈을 이야기하는 에세이.', 8, '다니엘 파커는 독서와 관련된 에세이를 다루는 작가이다.', 15, 16, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 3, '마음의 힐링', '조셉 킹', 15000, '심리학적 기법을 통해 마음의 상처를 치유하는 방법을 소개하는 에세이.', 9, '조셉 킹은 심리학자이자 에세이스트로, 마음의 치유를 돕는 저서를 집필한다.', 17, 18, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 4, '사랑과 연애의 과학', '사라 워너', 17000, '사랑과 연애의 심리학적 이면을 분석하는 에세이.', 10, '사라 워너는 사랑과 연애에 대한 심리학 연구를 진행하는 작가이다.', 19, 20, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 5, '인문학의 즐거움', '토마스 베커', 18000, '인문학의 다양한 영역과 그 속에서 얻을 수 있는 교훈을 소개하는 책.', 11, '토마스 베커는 인문학 전문가로 다양한 분야의 지식을 공유한다.', 21, 22, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 6, '심리학 이해하기', '마이클 리', 21000, '심리학의 기본 개념과 이론을 쉽게 이해할 수 있도록 설명하는 책.', 12, '마이클 리는 심리학 전문가로 다양한 심리학 서적을 집필하였다.', 23, 24, 'ACTIVE'),
('2023-02-01 00:01', '2021-01-01 00:01', 1, '현대 교육학의 기초', '제시카 헌터', 20000, '현대 교육학의 기초 개념과 방법론을 설명하는 교육학 입문서.', 13, '제시카 헌터는 교육학 전문가로 교육학 서적을 집필한다.', 25, 26, 'ACTIVE'),
